int main ( ) { int test ; scanf ( "%d" , & test ) ; if ( test < 1 || test > 1000 ) { return 0 ; } int ans [ 2 * test ] ; int t = 0 ; int j ; for ( j = 0 ; j < test ; j ++ ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; if ( a < 1 || a > 1000000 || b < 1 || b > 1000000 ) { return 0 ; } int i ; int gcd = 1 , lcm = 1 ; if ( a > b ) { int temp = a ; a = b ; b = temp ; } int product = a * b ; for ( i = a ; i > 0 ; i = b % i ) { if ( b % i == 0 ) { gcd = i ; } else { int temp = a ; a = b % i ; b = temp ; } } lcm = product / gcd ; ans [ t ++ ] = gcd ; ans [ t ++ ] = lcm ; } for ( t = 0 ; t < 2 * test ; t = t + 2 ) { printf ( "\n%d %d" , ans [ t ] , ans [ t + 1 ] ) ; } return 0 ; }