void sort ( int * array , int p ) { int i , temp , j ; for ( i = 1 ; i < p ; i ++ ) { temp = array [ i ] ; for ( j = i - 1 ; j >= 0 && temp < array [ j ] ; j -- ) array [ j + 1 ] = array [ j ] ; array [ j + 1 ] = temp ; } } int min ( int m , int n ) { if ( m < n ) return m ; else return n ; } int main ( ) { int test , i , p , s , sum = 0 , j , a ; scanf ( "%d" , & test ) ; while ( test -- ) { scanf ( "%d" , & p ) ; int array [ p ] ; for ( i = 0 ; i < p ; i ++ ) scanf ( "%d" , & array [ i ] ) ; sort ( array , p ) ; s = array [ 0 ] ; for ( i = 1 ; i < p ; i ++ ) { s = min ( s , array [ i ] ) ; sum = sum + s ; } printf ( "%d\n" , sum ) ; sum = 0 ; } return 0 ; }