int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int z , a [ 100 ] , i ; int count = 0 ; scanf ( "%d" , & z ) ; for ( i = 0 ; i < z ; i ++ ) { scanf ( "%d " , & a [ i ] ) ; } for ( i = 0 ; i < z - 1 ; i ++ ) { if ( a [ i ] < a [ i + 1 ] ) { count = count + a [ i ] ; a [ i + 1 ] = a [ i ] ; } else if ( a [ i ] > a [ i + 1 ] ) { count = count + a [ i + 1 ] ; } } printf ( "%d" , count ) ; printf ( "\n" ) ; } return 0 ; }