int main ( ) { int i , j , k ; unsigned long int n , t , a [ MAXROWS ] , count ; scanf ( "%lu" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lu" , & n ) ; count = 0 ; if ( n != 1 ) { for ( j = 0 ; j < n ; j ++ ) { scanf ( "%lu" , & a [ j ] ) ; } for ( j = 0 ; j < n ; j ++ ) { for ( k = 0 ; k < n ; k ++ ) { if ( j != k ) { if ( a [ j ] < a [ k ] ) { count ++ ; } } } } printf ( "%lu\n" , count ) ; } else if ( n == 1 && j == k && a [ j ] == a [ k ] ) { printf ( "1\n" ) ; } } return 0 ; }