main ( ) { int j , a , b , t , min , i , hcf = 1 , lcm ; printf ( "enter test case" ) ; scanf ( "%d" , & t ) ; for ( j = 1 ; j <= t ; j ++ ) { printf ( "enter two no." ) ; scanf ( "%d %d" , & a , & b ) ; min = a < b ? a : b ; for ( i = 1 ; i <= min ; i ++ ) { if ( a % i == 0 && b % i == 0 ) hcf = i ; } printf ( "hcfis %d \n" , hcf ) ; lcm = a * b / hcf ; printf ( "lcm is %d \n" , lcm ) ; } }