int main ( ) { long long int cost ; int t , i , j , temp , n ; scanf ( "%d" , & t ) ; while ( t -- ) { long int n ; scanf ( "%ld" , & n ) ; long int arr [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%ld" , & arr [ j ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < n ; j ++ ) { if ( arr [ j ] > arr [ j + 1 ] ) { temp = arr [ j + 1 ] ; arr [ j + 1 ] = arr [ j ] ; arr [ j ] = temp ; } } } cost = arr [ 0 ] * ( n - 1 ) ; printf ( "%lld\n" , cost ) ; } return 0 ; }