int main ( void ) { int test , row , i , j , k ; scanf ( "%d" , & test ) ; for ( i = 0 ; i < test ; i ++ ) { scanf ( "%d" , & row ) ; int array [ row ] [ row ] ; int col = 0 ; for ( j = 0 ; j < row ; j ++ ) { for ( k = 0 ; k < col + 1 ; k ++ ) { scanf ( "%d" , & array [ j ] [ k ] ) ; } col = col + 1 ; } int sum = 0 ; for ( j = 0 , k = 0 ; j < row ; j ++ ) { sum = sum + array [ j ] [ k ] ; } int flag = 0 ; int tempsum ; for ( k = 0 ; k < row - 1 ; k ++ ) { int useflag = flag + 1 ; tempsum = 0 ; for ( j = row - 1 ; j >= 0 ; j -- ) { tempsum = tempsum + array [ j ] [ useflag ] ; if ( useflag == 0 ) { useflag = 0 ; } else { useflag = useflag - 1 ; } } flag = flag + 1 ; if ( tempsum > sum ) { sum = tempsum ; } } printf ( "%d\n" , sum ) ; } return 0 ; }