int main ( ) { int t ; long a , b , x , y , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a >= b ) { x = a ; y = b ; while ( ! ( x % a == 0 && x % b == 0 ) ) x ++ ; while ( ! ( a % y == 0 && b % y == 0 ) ) y -- ; lcm = x ; gcd = y ; } else { x = b ; y = a ; while ( ! ( x % a == 0 && x % b == 0 ) ) x ++ ; while ( ! ( a % y == 0 && b % y == 0 ) ) y -- ; lcm = x ; gcd = y ; } printf ( "%d %d\n" , gcd , lcm ) ; } }