int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , i , s , hcf , lcm ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) s = a ; else s = b ; for ( i = 1 ; i <= s ; i ++ ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) { hcf = i ; } } printf ( "%d\n" , hcf ) ; lcm = ( a * b ) / hcf ; printf ( "%d\n" , lcm ) ; } return 0 ; }