int main ( ) { int i , j , k , m , n , max ; scanf ( "%d" , & n ) ; int sum [ n ] ; for ( k = 0 ; k < n ; k ++ ) { scanf ( "%d" , & m ) ; sum [ k ] = 0 ; int a [ m ] ; for ( i = 0 ; i < m ; i ++ ) { a [ i ] = 0 ; } for ( i = 1 ; i <= m ; i ++ ) { max = a [ 0 ] ; for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] ) ; if ( max < a [ j ] ) max = a [ j ] ; } sum [ k ] += max ; } } for ( i = 0 ; i < n ; i ++ ) { printf ( "%d\n" , sum [ i ] ) ; } return 0 ; }