int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int cost , i ; cost = 0 ; long long int n ; scanf ( "%lld" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { cost = cost + a [ i + 1 ] ; n - 1 ; } else if ( a [ i ] < a [ i + 1 ] ) { a [ i + 1 ] = a [ i ] ; cost = cost + a [ i ] ; n - 1 ; } } printf ( "%lld\n" , cost ) ; } }