int get_gcd ( int a , int b ) { if ( b == 0 ) return a ; else return get_gcd ( b , a % b ) ; } int main ( ) { int t , a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; if ( a == 0 ) printf ( "%d %d\n" , b , 0 ) ; else if ( b == 0 ) printf ( "%d %d\n" , a , 0 ) ; else { int gcd = get_gcd ( a , b ) ; int lcm = a * b ; lcm /= gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } } return 0 ; }