int main ( ) { int a , i , min , no1 , no2 , lcm [ 100 ] = { 0 } , c , gcd [ 100 ] = { 0 } ; scanf ( "%d" , & a ) ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d" , & no1 ) ; scanf ( "%d" , & no2 ) ; if ( no1 > no2 ) min = no2 ; else min = no1 ; for ( c = 1 ; c <= min ; c ++ ) { if ( no1 % c == 0 && no2 % c == 0 ) { gcd [ i ] = c ; } } if ( no1 % no2 == 0 ) lcm [ i ] = no1 ; else if ( no2 % no1 == 0 ) lcm [ i ] = no2 ; else lcm [ i ] = no1 * no2 ; } for ( i = 0 ; i < a ; i ++ ) { printf ( "%d %d\n" , gcd [ i ] , lcm [ i ] ) ; } return 0 ; }