int main ( ) { int i , t , j , k ; int n [ 10000 ] ; int a [ 10000 ] ; scanf ( "%d\n" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d\n" , & n [ i ] ) ; for ( j = 0 ; j < n [ i ] ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } int sum ; sum = 0 ; for ( j = 0 ; j < n [ i ] - 1 ; j ++ ) { if ( a [ j ] < a [ j + 1 ] ) sum = sum + a [ j ] ; else sum = sum + a [ j + 1 ] ; } printf ( "%d\n" , sum ) ; printf ( "\n" ) ; } return 0 ; }