long int gcdf ( long int , long int ) ; int main ( ) { long int t , a , b , i , lc , gc ; scanf ( "%ld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; gc = gcdf ( a , b ) ; lc = ( a * b ) / gc ; printf ( "%ld %ld\n" , gc , lc ) ; } } long int gcdf ( long int m , long int n ) { long int temp ; if ( m < n ) { temp = m ; m = n ; n = temp ; } while ( ( m % n ) != 0 ) { temp = m ; m = n ; n = temp % n ; } return n ; }