long int hcf ( long int , long int ) ; long long int lcm ( long int , long int ) ; int main ( ) { long long int l ; long int t , h , a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; l = lcm ( a , b ) ; h = hcf ( a , b ) ; printf ( "%ld %lld\n" , h , l ) ; } return 0 ; } long int hcf ( long int a , long int b ) { if ( b == 0 ) return a ; else return hcf ( b , a % b ) ; } long long int lcm ( long int a , long int b ) { long long int lm = 1 , i ; for ( i = 2 ; a > b ? i <= a : i <= b ; ) { if ( ( a % i == 0 ) || ( b % i == 0 ) ) { lm = lm * i ; if ( a % i == 0 ) a = a / i ; if ( b % i == 0 ) b = b / i ; continue ; } i ++ ; } return lm ; }