int findLength ( int a ) { int sum = 0 ; for ( int i = 1 ; i <= a ; i ++ ) { sum += i ; } return sum ; } int main ( ) { int n ; scanf ( "%d" , & n ) ; int t [ n ] ; int res [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & t [ i ] ) ; int l = findLength ( t [ i ] ) ; int trian [ l ] ; for ( int k = 0 ; k < l ; k ++ ) { scanf ( "%d" , & trian [ k ] ) ; } int j = 1 , k = 1 ; int sum = trian [ 0 ] ; while ( j < t [ i ] ) { if ( trian [ k ] > trian [ k + 1 ] ) { sum += trian [ k ] ; k += ++ j ; } else { sum += trian [ k + 1 ] ; k += ++ j + 1 ; } } res [ i ] = sum ; } for ( int i = 0 ; i < n ; i ++ ) printf ( "%d\n" , res [ i ] ) ; }