int main ( ) { long long int t , gcd , lcm , i , temp1 , temp2 , n1 , n2 ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & n1 , & n2 ) ; temp1 = n1 ; temp2 = n2 ; while ( temp1 != temp2 ) { if ( temp1 > temp2 ) { temp1 = temp1 - temp2 ; } else { temp2 = temp2 - temp1 ; } } gcd = temp1 ; lcm = ( n1 * n2 ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }