int main ( void ) { int * a , i , j , t ; long long count ; long n ; scanf ( "%d" , & t ) ; if ( t < 1 || t > 5 ) exit ( 0 ) ; while ( t > 0 ) { count = 0 ; scanf ( "%d" , & n ) ; if ( n < 1 || n > 100000 ) exit ( 0 ) ; else { a = ( int * ) malloc ( n * sizeof ( int ) ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) for ( j = 0 ; j < n ; j ++ ) if ( a [ i ] <= a [ j ] ) count ++ ; } printf ( "%lld\n" , count ) ; t -- ; } return 0 ; }