int sumtran ( ) ; int sumtran ( ) { int n , i , j , a , b ; scanf ( "%d" , & n ) ; int arr1 [ 100 ] [ 100 ] , arr2 [ 100 ] [ 100 ] ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & arr1 [ i ] [ j ] ) ; } } for ( i = 0 ; i < n ; i ++ ) { arr2 [ n - 1 ] [ i ] = arr1 [ n - 1 ] [ i ] ; } for ( i = n - 2 ; i >= 0 ; i -- ) { for ( j = 0 ; j <= i ; j ++ ) { a = arr2 [ i + 1 ] [ j ] ; b = arr2 [ i + 1 ] [ j + 1 ] ; arr2 [ i ] [ j ] = arr1 [ i ] [ j ] + ( a > b ? a : b ) ; } } return arr2 [ 0 ] [ 0 ] ; } int main ( ) { int t , count ; scanf ( "%d" , & t ) ; count = 1 ; int ar [ 1500 ] ; while ( count <= t ) { ar [ count ] = sumtran ( ) ; count ++ ; } count = 1 ; while ( count <= t ) { printf ( "%d\n" , ar [ count ] ) ; count ++ ; } return 0 ; }