int main ( void ) { long long int n , a [ 50000 ] , t , sum , i ; scanf ( "%lld" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%lld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] < a [ i + 1 ] ) { sum = sum + a [ i ] ; a [ i + 1 ] = a [ i ] ; } else { sum = sum + a [ i + 1 ] ; } } printf ( "%lld\n" , sum ) ; } return 0 ; }