long hcf ( long a , long b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; else if ( a > b ) return hcf ( a - b , b ) ; return hcf ( a , b - a ) ; } int main ( ) { int T ; long long A , B ; scanf ( "%d" , & T ) ; for ( int i = 0 ; i < T ; i ++ ) { scanf ( "%lld %lld" , & A , & B ) ; long long gcd = 0 , lcm = 0 ; gcd = hcf ( A , B ) ; lcm = ( A * B ) / gcd ; printf ( "%lld %lld \n" , gcd , lcm ) ; } }