int gdc ( int a , int b ) { int i , g ; for ( i = 1 ; i <= a ; i ++ ) { if ( a % i == 0 ) { if ( b % i == 0 ) { g = i ; } } return ( g ) ; } } int lcm ( int a , int b ) { int x = 1 , i ; for ( i = 2 ; i < a ; i ++ ) { if ( a % i == 0 ) { if ( b % i == 0 ) { a = a / i ; b = b / i ; x = x * i ; } } } return ( a * b * x ) ; } int main ( ) { unsigned int s ; scanf ( "%d" , & s ) ; while ( s -- ) { int a , b , l , g ; scanf ( "%d%d" , & a , & b ) ; l = lcm ( a , b ) ; g = gdc ( a , b ) ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }