int sum [ 10000 ] , a [ 10000 ] ; int main ( ) { int i , count ; scanf ( "%d" , & count ) ; for ( i = 0 ; i < count ; i ++ ) { int t , j , k , r = 0 ; scanf ( "%d" , & t ) ; for ( j = 0 ; j < t ; j ++ ) sum [ j ] = 0 ; for ( j = 0 ; j < t ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) scanf ( "%d" , & a [ k ] ) ; for ( k = j ; k >= 0 ; k -- ) { if ( k == 0 && k == j ) sum [ k ] = a [ k ] ; else if ( k == j ) sum [ k ] = sum [ k - 1 ] + a [ k ] ; else if ( k == 0 ) sum [ k ] += a [ k ] ; else if ( sum [ k ] > sum [ k - 1 ] ) sum [ k ] += a [ k ] ; else sum [ k ] = sum [ k - 1 ] + a [ k ] ; } } int max = 0 ; for ( j = 0 ; j < t ; j ++ ) if ( max < sum [ j ] ) max = sum [ j ] ; printf ( "%d\n" , max ) ; } return 0 ; }