int main ( ) { int t , cost , i , n , min = 50001 ; scanf ( "%d" , & t ) ; while ( t >= 1 ) { min = 50001 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } cost = min * ( n - 1 ) ; printf ( "%d" , cost ) ; t -- ; } }