int main ( ) { int t , i , j , max , sum , m , k , n ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; max = 0 ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( m = 0 ; m < n ; m ++ ) { sum = 0 ; k = m ; j = 0 ; for ( i = 0 ; i < n ; i ++ ) { sum = sum + a [ i ] [ j ] ; if ( k > 0 ) k -- ; else j ++ ; } if ( sum > max ) max = sum ; } printf ( "%d" , max ) ; } return 0 ; }