int main ( void ) { int casee ; int test_cases ; scanf ( "%d" , & casee ) ; int k ; for ( k = 0 ; k < casee ; k ++ ) { scanf ( "%d" , & test_cases ) ; int num [ test_cases ] [ test_cases ] ; int i , j , sum = 0 ; for ( j = 0 ; j < test_cases ; j ++ ) { for ( i = 0 ; i <= j ; i ++ ) { scanf ( "%d" , & num [ j ] [ i ] ) ; } } i = 0 ; int sumd , sumr , a , b ; sumd = sumr = num [ 0 ] [ 0 ] ; for ( j = 1 ; j < test_cases - 1 ; j ++ ) { for ( ; i < j ; i ++ ) { sumd = sumd + num [ j ] [ i ] ; sumr = sumr + num [ j ] [ i + 1 ] ; if ( sumd + num [ j + 1 ] [ i ] > sumd + num [ j + 1 ] [ i + 1 ] ) a = sumd + num [ j + 1 ] [ i ] ; else a = sumd + num [ j + 1 ] [ i + 1 ] ; if ( sumr + num [ j + 1 ] [ i + 1 ] > sumr + num [ j + 1 ] [ i + 2 ] ) b = sumr + num [ j + 1 ] [ i + 1 ] ; else b = sumr + num [ j + 1 ] [ i + 2 ] ; if ( a > b ) { sum = sumd ; break ; } else { sum = sumr ; i = i + 1 ; break ; } } } if ( num [ test_cases - 1 ] [ i ] > num [ test_cases - 1 ] [ i + 1 ] ) { sum = sum + num [ test_cases - 1 ] [ i ] ; printf ( "%d\n" , sum ) ; } else { sum = sum + num [ test_cases - 1 ] [ i + 1 ] ; printf ( "%d\n" , sum ) ; } } return 0 ; }