main ( ) { int t , n , i , j , k , sum = 0 , q = 0 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ j ] ) ; while ( n != 1 ) { n -- ; if ( a [ q ] >= a [ q + 1 ] ) { sum += a [ q + 1 ] ; for ( k = q ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; q ++ ; } else { sum += a [ q ] ; for ( k = q + 1 ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; q ++ ; } } printf ( "%d\n" , sum ) ; sum = 0 ; q = 0 ; } }