int main ( ) { int n , i , j ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { int sum = 0 ; int m = 0 ; int t ; scanf ( "%d" , & t ) ; int * a = ( int * ) malloc ( sizeof ( int ) * ( ( t * ( t + 1 ) ) / 2 ) ) ; for ( j = 0 ; j < t ; j ++ ) { int k = j + 1 ; while ( k != 0 ) { scanf ( "%d" , & a [ m ] ) ; m ++ ; k -- ; } } int index = 0 ; sum = sum + a [ index ] ; index = 1 ; j = 1 ; while ( j != t ) { if ( a [ index * 2 - 1 ] > a [ index * 2 ] ) { sum = sum + a [ index * 2 - 1 ] ; index = index * 2 - 1 ; j ++ ; } else { sum = sum + a [ index * 2 ] ; index = index * 2 ; j ++ ; } } printf ( "%d" , sum ) ; } return 0 ; }