void main ( ) { int t , i , j , n , a [ 100000 ] , num [ 100000 ] , temp ; unsigned int sum ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; sum = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { num [ i ] = a [ i + 1 ] ; } else { num [ i ] = a [ i ] ; temp = a [ i ] ; a [ i ] = a [ i + 1 ] ; a [ i + 1 ] = temp ; } } for ( i = 0 ; i < n - 1 ; i ++ ) { sum = sum + num [ i ] ; } printf ( "%u\n" , sum ) ; } }