int main ( ) { int t , n , i , j , k = 0 ; long long int a [ 100000 ] , ans ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%lld" , & a [ j ] ) ; } for ( j = 0 ; j < n ; j ++ ) { if ( j == 0 ) ans = 1 ; else { if ( a [ j ] < a [ j - 1 ] ) k = j ; ans += ( 1 + j - k ) ; } } printf ( "%lld\n" , ans ) ; } return 0 ; }