int main ( void ) { int x ; scanf ( "%d" , & x ) ; while ( x -- ) { int k , y , z , g , f , a , lcm ; scanf ( "%d%d" , & k , & y ) ; if ( k > y ) { z = y ; for ( g = 0 ; g < z ; g ++ ) { if ( k % g == 0 && y % g == 0 ) f = g ; } } else { z = k ; for ( g = 0 ; g < z ; g ++ ) { if ( k % g == 0 && y % g == 0 ) f = g ; } } a = k * y ; lcm = a / f ; printf ( "%d%d" , f , lcm ) ; } return 0 ; }