int gcd ( int m , int n ) { int temp ; while ( n ) { temp = n ; n = m % n ; m = temp ; } return m ; } int main ( ) { int i , a , t , x , y ; printf ( "\n Enter the no. of test cases:\n" ) ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { printf ( "\n Enter the two nos.\n" ) ; scanf ( "%d%d" , & x , & y ) ; a = gcd ( x , y ) ; printf ( "HCF: %d" , a ) ; printf ( "\nLCM: %d" , ( x * y ) / a ) ; } return 0 ; }