int main ( ) { int a , b , c , e , i , f , num1 , num2 ; scanf ( "%d" , & a ) ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d%d" , & b , & c ) ; if ( b > c ) { num1 = b ; num2 = c ; } if ( b == c ) { printf ( "%d %d\n" , b , c ) ; } if ( c == 0 ) { printf ( "%d %d\n" , b , 1 ) ; } if ( b == 0 ) { printf ( "%d %d\n" , c , 1 ) ; } else { num1 = c ; num2 = b ; } e = ( num1 % num2 ) ; while ( e != 0 ) { num1 = num2 ; num2 = e ; e = ( num1 % num2 ) ; } f = ( b * c ) / num2 ; printf ( "%d %d\n" , num2 , f ) ; } return 0 ; }