int main ( ) { int i , t , j ; long int lcm , hcf , a , b ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a > b ) { if ( a % b == 0 ) { hcf = b ; } else { for ( j = b ; j >= 0 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { hcf = j ; break ; } } } } else { if ( b % a == 0 ) { hcf = a ; } else { for ( j = a ; j >= 0 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { hcf = j ; break ; } } } } lcm = a * b / hcf ; printf ( "%ld %ld\n" , hcf , lcm ) ; } return 0 ; }