int main ( ) { int t , a , b , gcd , m , n ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; m = a ; n = b ; while ( ( a != 0 ) || ( b != 0 ) ) { a = a % b ; if ( a == 0 ) { gcd = b ; break ; } b = b % a ; if ( b == 0 ) { gcd = a ; break ; } } printf ( "%ld %ld\n" , gcd , m * n / gcd ) ; } return 0 ; }