int main ( ) { unsigned long long int l , c , j , a , b , max , min ; int i , n ; unsigned long long int p ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%I64d %I64d" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } l = max ; for ( j = 0 ; j < l ; j ++ ) { if ( max % min == 0 ) { p = ( a * b ) / min ; printf ( "%I64d %I64d\n" , min , p ) ; j = max ; } c = max % min ; max = min ; min = c ; } } }