int main ( ) { int t , i , n , j , temp ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , cnt = 0 ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; for ( j = 0 ; j < n ; j ++ ) { if ( j + 1 < n && arr [ j ] > arr [ j + 1 ] ) cnt = cnt + arr [ j + 1 ] ; if ( j + 1 < n && arr [ j ] < arr [ j + 1 ] ) { cnt = cnt + arr [ j ] ; temp = arr [ j + 1 ] ; arr [ j + 1 ] = arr [ j ] ; arr [ j ] = temp ; } } printf ( "%d\n" , cnt ) ; } return 0 ; }