int main ( ) { int a , b , i , p , j , max , min , n ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } for ( j = min ; j > 0 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { printf ( "%d " , j ) ; j = 0 ; } } for ( p = max ; p <= ( a * b ) ; p = p + max ) { if ( p % a == 0 && p % b == 0 ) { printf ( "%d\n" , p ) ; break ; } } } }