long long int gcd ( long long int a , long long int b ) { if ( a == 0 || b == 0 ) { return ( 0 ) ; } if ( a == b ) { return a ; } if ( a > b ) { return gcd ( a - b , b ) ; } else { return gcd ( a , b - a ) ; } } int main ( ) { long long int a , b , t , i , h , lcm ; scanf ( "%lld" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; h = gcd ( a , b ) ; lcm = ( a * b ) / h ; printf ( "%lld %lld\n" , h , lcm ) ; } }