int greater ( int a , int b ) { if ( a >= b ) return a ; else return b ; } int lesser ( int a , int b ) { if ( a <= b ) return a ; else return b ; } int gcd ( int a , int b ) { int i , n ; n = lesser ( a , b ) ; i = 1 ; while ( i <= n ) { if ( a % i == 0 && b % i == 0 ) { return i ; } i ++ ; } } int lcm ( int a , int b ) { int i , n ; n = greater ( a , b ) ; i = n ; while ( i ) { if ( i % a == 0 && i % b == 0 ) { return i ; break ; } i ++ ; } } int main ( ) { int i , t , a , b ; int r1 , r2 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; r1 = gcd ( a , b ) ; r2 = lcm ( a , b ) ; printf ( "%d %d\n" , r1 , r2 ) ; } }