int main ( ) { int t , a , b , i = 1 ; long long l , g ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; if ( a > b ) { for ( i = 1 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; } } } else { for ( i = 1 ; i <= a ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; } } } l = ( a * b ) / g ; printf ( "%lld %lld\n" , g , l ) ; } }