long long int hcf ( long long int a , long long int b ) { if ( b == 0 ) return a ; long long int rem = a % b ; return hcf ( b , rem ) ; } int main ( ) { int test , i ; scanf ( "%d" , & test ) ; long long int first , second , max , min ; for ( i = 0 ; i < test ; i ++ ) { scanf ( "%lld %lld" , & first , & second ) ; max = first > second ? first : second ; min = first <= second ? first : second ; long long int gcd = hcf ( max , min ) ; printf ( "%lld %lld\n" , gcd , ( first * second ) / gcd ) ; } return 0 ; }