int main ( ) { int n , i , j , x ; long long a [ 10000 ] ; long long b [ 10000 ] ; long long gcd [ 10000 ] ; long long lcm [ 10000 ] ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld %lld" , & a [ i ] , & b [ i ] ) ; if ( a [ i ] < b [ i ] ) { x = a [ i ] ; } else { x = b [ i ] ; } for ( ; x >= 1 ; x -- ) { if ( a [ i ] % x == 0 && b [ i ] % x == 0 ) { gcd [ i ] = x ; lcm [ i ] = ( a [ i ] * b [ i ] ) / gcd [ i ] ; break ; } } } for ( j = 0 ; j < n ; j ++ ) { printf ( "%lld %lld\n" , gcd [ j ] , lcm [ j ] ) ; } return 0 ; }