int main ( ) { int t , n ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int i , sum = 0 , max ; int arr [ n ] [ n ] ; memset ( arr , 0 , sizeof ( arr ) ) ; int j = 0 ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & arr [ i ] [ j ] ) ; } max = arr [ 0 ] [ 0 ] + arr [ 1 ] [ 0 ] + arr [ 1 ] [ 1 ] ; for ( i = 1 ; i < n - 1 ; i ++ ) { sum = arr [ i ] [ 0 ] + arr [ i + 1 ] [ 0 ] + arr [ i + 1 ] [ 1 ] ; if ( sum > max ) { max = sum ; } } printf ( "%d\n" , max ) ; } return 0 ; }