int main ( ) { int a [ 100 ] [ 100 ] ; int i , k , j , n , t , sum ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } sum = a [ 0 ] [ 0 ] ; k = 1 , j = 0 ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ k ] [ j ] > a [ k ] [ j + 1 ] ) { sum += a [ k ] [ j ] ; j = j ; } else { sum += a [ k ] [ j + 1 ] ; j = j + 1 ; } k ++ ; } printf ( "\n%d" , sum ) ; } return 0 ; }