long long int gcd ( long long int x , long long int y ) { if ( y == 0 ) return x ; else return gcd ( y , x % y ) ; } long long int lcm ( long long int x , int y ) { return ( ( x * y ) / gcd ( x , y ) ) ; } main ( ) { long long int a , b , t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; printf ( "%d %d\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } }