int main ( ) { int n1 , n2 , i , gcd , lcm , t , z ; scanf ( "%d" , & t ) ; for ( z = 0 ; z < t ; z ++ ) { scanf ( "%d %d" , & n1 , & n2 ) ; for ( i = 1 ; i <= n1 && i <= n2 ; ++ i ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } lcm = ( n1 * n2 ) / gcd ; while ( n1 != n2 ) { if ( n1 > n2 ) n1 -= n2 ; else n2 -= n1 ; } printf ( "%d %d\n" , n1 , lcm ) ; } return 0 ; }