int gcd ( long int a , long int b ) { int temp ; if ( b > a ) { temp = b ; b = a ; a = temp ; } if ( a % b == 0 ) { return b ; } else if ( b == 0 ) { return a ; } return gcd ( a % b , b ) ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a , b ; scanf ( "%ld %ld" , & a , & b ) ; int hcf = gcd ( a , b ) ; long int mult = a * b ; long int lcm = mult / hcf ; printf ( "%d %ld\n" , hcf , lcm ) ; } return 0 ; }