int main ( ) { int t , n ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int k = n * ( n + 1 ) / 2 ; int arr [ k ] , i ; for ( i = 0 ; i < k ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } int s = 1 , nxr , nxb ; while ( s != 0 ) { s = k - ( 2 * n ) + 1 ; nxb = s + n - 1 ; nxr = nxb + 1 ; arr [ s ] = ( arr [ s ] + arr [ nxb ] ) > ( arr [ s ] + arr [ nxr ] ) ? arr [ s ] + arr [ nxb ] : arr [ s ] + arr [ nxr ] ; k = k - n ; n = n - 1 ; } printf ( "%d" , arr [ 0 ] ) ; } }